// Extract and validate input parameters
const targetUrl = $input.first().json.target_url || $input.first().json.body?.target_url;
const location = $input.first().json.location || $input.first().json.body?.location || 'United Kingdom';
const reportType = $input.first().json.report_type || $input.first().json.body?.report_type || 'onboarding';
const email = $input.first().json.email || $input.first().json.body?.email;

// NEW: Report audience parameter (client or internal)
const reportAudience = $input.first().json.report_audience || $input.first().json.body?.report_audience || 'client';

// Validate inputs
if (!targetUrl) {
  throw new Error('target_url is required');
}

// Clean URL
const cleanUrl = targetUrl.replace(/^https?:\/\//, '').replace(/\/$/, '');

return {
  json: {
    target_url: cleanUrl,
    location: location,
    report_type: reportType.toLowerCase(),
    report_audience: reportAudience.toLowerCase(), // 'client' or 'internal'
    email: email,
    timestamp: new Date().toISOString(),
  }
};

// Wait for OnPage task to complete (5 minutes)
await new Promise(resolve => setTimeout(resolve, 300000));
return $input.all();

// Parse Domain Rank Overview - Extract relevant metrics
const input = $input.first().json;

// Check if API call was successful
if (input.status_code !== 20000 || !input.tasks?.[0]?.result?.[0]) {
  return {
    success: false,
    error: input.tasks?.[0]?.status_message || 'Domain Rank API failed',
    domainRank: null
  };
}

const result = input.tasks[0].result[0];
const metrics = result.items?.[0]?.metrics?.organic || {};

// Extract only relevant data for the report
return {
  success: true,
  domainRank: {
    // Basic Info
    target: result.target,
    location: result.location_code,
    language: result.language_code,

    // Position Distribution
    positions: {
      top3: (metrics.pos_1 || 0) + (metrics.pos_2_3 || 0),
      top10: (metrics.pos_1 || 0) + (metrics.pos_2_3 || 0) + (metrics.pos_4_10 || 0),
      page2: metrics.pos_11_20 || 0,  // Opportunities!
      page3: metrics.pos_21_30 || 0,
      page4Plus: (metrics.pos_31_40 || 0) + (metrics.pos_41_50 || 0) + 
                 (metrics.pos_51_60 || 0) + (metrics.pos_61_70 || 0) + 
                 (metrics.pos_71_80 || 0) + (metrics.pos_81_90 || 0) + 
                 (metrics.pos_91_100 || 0)
    },

    // Traffic & Value
    estimatedTrafficValue: Math.round(metrics.etv || 0),
    totalKeywords: metrics.count || 0,
    estimatedPaidTrafficCost: Math.round((metrics.estimated_paid_traffic_cost || 0) * 100) / 100,

    // Keyword Movement
    movement: {
      newKeywords: metrics.is_new || 0,
      improved: metrics.is_up || 0,
      declined: metrics.is_down || 0,
      lost: metrics.is_lost || 0
    }
  }
};

// Parse Ranked Keywords - Extract and analyze keyword data
const input = $input.first().json;

// Check if API call was successful
if (input.status_code !== 20000 || !input.tasks?.[0]?.result?.[0]) {
  return {
    success: false,
    error: input.tasks?.[0]?.status_message || 'Ranked Keywords API failed',
    keywords: null
  };
}

const result = input.tasks[0].result[0];
const items = result.items || [];
const target = result.target || '';

// Extract domain name for branded keyword detection
const domainName = target.replace(/^www\./, '').split('.')[0].toLowerCase();

// Initialize counters
let brandedCount = 0;
let unbrandedCount = 0;
let totalSearchVolume = 0;
let totalETV = 0;

// Position buckets
const positionBuckets = {
  top3: [],
  top10: [],
  page2Opportunities: [],  // Positions 11-20 - Ready to push!
  page3: [],
  deepPositions: []
};

// Process each keyword
const processedKeywords = items.map(item => {
  const keyword = item.keyword_data?.keyword || '';
  const keywordLower = keyword.toLowerCase();
  const searchVolume = item.keyword_data?.keyword_info?.search_volume || 0;
  const rank = item.ranked_serp_element?.serp_item?.rank_absolute || 999;
  const url = item.ranked_serp_element?.serp_item?.url || '';
  const etv = item.ranked_serp_element?.serp_item?.etv || 0;
  const cpc = item.keyword_data?.keyword_info?.cpc || 0;
  const difficulty = item.keyword_data?.keyword_properties?.keyword_difficulty || 0;
  const intent = item.keyword_data?.search_intent_info?.main_intent || 'unknown';
  const isNew = item.ranked_serp_element?.serp_item?.rank_changes?.is_new || false;

  // Determine if branded
  const isBranded = keywordLower.includes(domainName);
  if (isBranded) {
    brandedCount++;
  } else {
    unbrandedCount++;
  }

  totalSearchVolume += searchVolume;
  totalETV += etv;

  const keywordData = {
    keyword,
    rank,
    searchVolume,
    etv: Math.round(etv * 100) / 100,
    cpc: Math.round(cpc * 100) / 100,
    difficulty,
    intent,
    url,
    isBranded,
    isNew
  };

  // Categorize by position
  if (rank <= 3) {
    positionBuckets.top3.push(keywordData);
  } else if (rank <= 10) {
    positionBuckets.top10.push(keywordData);
  } else if (rank <= 20) {
    positionBuckets.page2Opportunities.push(keywordData);
  } else if (rank <= 30) {
    positionBuckets.page3.push(keywordData);
  } else {
    positionBuckets.deepPositions.push(keywordData);
  }

  return keywordData;
});

// Sort opportunities by search volume (highest first)
positionBuckets.page2Opportunities.sort((a, b) => b.searchVolume - a.searchVolume);
positionBuckets.top10.sort((a, b) => a.rank - b.rank);
positionBuckets.top3.sort((a, b) => a.rank - b.rank);

// Calculate branded/unbranded percentages
const totalKeywords = items.length;
const brandedPercent = totalKeywords > 0 ? Math.round((brandedCount / totalKeywords) * 100) : 0;
const unbrandedPercent = totalKeywords > 0 ? Math.round((unbrandedCount / totalKeywords) * 100) : 0;

return {
  success: true,
  keywords: {
    // Summary Stats
    summary: {
      totalKeywords,
      totalSearchVolume,
      totalETV: Math.round(totalETV * 100) / 100,
      brandedCount,
      unbrandedCount,
      brandedPercent,
      unbrandedPercent
    },

    // Position Distribution
    distribution: {
      top3Count: positionBuckets.top3.length,
      top10Count: positionBuckets.top3.length + positionBuckets.top10.length,
      page2Count: positionBuckets.page2Opportunities.length,
      page3Count: positionBuckets.page3.length,
      deepCount: positionBuckets.deepPositions.length
    },

    // Top Performers (Top 10 by rank)
    topPerformers: [...positionBuckets.top3, ...positionBuckets.top10].slice(0, 10),

    // Quick Win Opportunities (Page 2 keywords with high volume)
    quickWinOpportunities: positionBuckets.page2Opportunities.slice(0, 10),

    // All keywords for detailed analysis
    allKeywords: processedKeywords.slice(0, 50)  // Limit to 50 for report
  }
};

// Parse OnPage Summary - Extract technical SEO metrics
const input = $input.first().json;
const task = input.tasks?.[0];

// Check if task is still in queue
if (task?.status_code === 40602) {
  return {
    success: false,
    status: 'in_queue',
    message: 'OnPage analysis is still processing. Please wait and retry.',
    onPage: null
  };
}

// Check if API call was successful
if (input.status_code !== 20000 || !task?.result?.[0]) {
  return {
    success: false,
    status: 'error',
    error: task?.status_message || 'OnPage API failed',
    onPage: null
  };
}

const result = task.result[0];
const crawl = result.crawl_progress || {};

return {
  success: true,
  status: 'complete',
  onPage: {
    // Crawl Info
    crawlProgress: crawl,
    pagesCrawled: result.pages_crawled || 0,
    pagesInQueue: result.pages_in_queue || 0,

    // Technical Issues
    issues: {
      critical: result.page_metrics?.checks?.critical || 0,
      warnings: result.page_metrics?.checks?.warnings || 0,
      notices: result.page_metrics?.checks?.notices || 0
    },

    // Page Metrics
    pageMetrics: {
      brokenLinks: result.page_metrics?.broken_links || 0,
      brokenResources: result.page_metrics?.broken_resources || 0,
      duplicateTitle: result.page_metrics?.duplicate_title || 0,
      duplicateDescription: result.page_metrics?.duplicate_description || 0,
      duplicateContent: result.page_metrics?.duplicate_content || 0
    },

    // Performance
    performance: {
      avgPageLoadTime: result.page_metrics?.page_timing?.time_to_interactive || 0,
      https: result.page_metrics?.checks?.no_https || 0,
      mobileUsability: result.page_metrics?.checks?.is_mobile_friendly || false
    }
  }
};

// Parse Backlinks Summary - Extract backlink metrics
const input = $input.first().json;
const task = input.tasks?.[0];

// Check for access denied error
if (task?.status_code === 40204) {
  return {
    success: false,
    error: 'Backlinks API requires subscription activation',
    message: 'Visit https://app.dataforseo.com/backlinks-subscription to activate',
    backlinks: null
  };
}

// Check if API call was successful
if (input.status_code !== 20000 || !task?.result?.[0]) {
  return {
    success: false,
    error: task?.status_message || 'Backlinks API failed',
    backlinks: null
  };
}

const result = task.result[0];

return {
  success: true,
  backlinks: {
    // Core Metrics
    totalBacklinks: result.backlinks || 0,
    referringDomains: result.referring_domains || 0,
    referringMainDomains: result.referring_main_domains || 0,
    referringIPs: result.referring_ips || 0,

    // Link Types
    dofollow: result.dofollow || 0,
    nofollow: result.nofollow || 0,

    // Quality Indicators
    rank: result.rank || 0,
    brokenBacklinks: result.broken_backlinks || 0,

    // Calculations
    dofollowPercent: result.backlinks > 0 
      ? Math.round((result.dofollow / result.backlinks) * 100) 
      : 0
  }
};

// Aggregate All Data - Combine into single object
const allInputs = $input.all();

// Get data from Validate Input
const validateInputData = $('Validate Input').first().json;

const aggregatedData = {
  onPage: null,
  domainRank: null,
  keywords: null,
  backlinks: null,
  metadata: {
    generatedAt: new Date().toISOString(),
    reportType: validateInputData.report_type || 'onboarding',
    reportAudience: validateInputData.report_audience || 'client',
    targetUrl: validateInputData.target_url
  }
};

for (const item of allInputs) {
  const data = item.json;

  if (data.onPage !== undefined) {
    aggregatedData.onPage = data;
  } else if (data.domainRank !== undefined) {
    aggregatedData.domainRank = data;
  } else if (data.keywords !== undefined) {
    aggregatedData.keywords = data;
  } else if (data.backlinks !== undefined) {
    aggregatedData.backlinks = data;
  }
}

return aggregatedData;

// Convert LLM output to Markdown file
const markdown = $input.first().json.text;
const website = $('Generate Prompt').first().json.targetWebsite || 'website';
const date = new Date().toISOString().split('T')[0];

// Create clean filename
const cleanWebsite = website.replace(/[^a-zA-Z0-9]/g, '_').replace(/_+/g, '_');
const filename = `SEO_Onboarding_Report_${cleanWebsite}_${date}.md`;

// Convert markdown text to base64 for binary
const base64Content = Buffer.from(markdown, 'utf8').toString('base64');

return {
  json: {
    filename: filename,
    targetWebsite: website,
    generatedAt: date,
    markdownContent: markdown
  },
  binary: {
    data: {  // ‚Üê Changed from 'file' to 'data'
      data: base64Content,
      mimeType: 'text/markdown',
      fileName: filename,
      fileExtension: 'md'
    }
  }
};

// Get Latest Previous Report - use result from Filter Files

// 1) Aggregated SEO data from Aggregate Data1
const aggregatedData = $('Aggregate Data1').first().json;

// 2) File chosen by Filter Files (comes in as this node's input)
const input = $input.first().json;

// If Filter Files found a match, it sets matched: true and provides file
let previousReportFile = null;

if (input && input.matched === true && input.file) {
  previousReportFile = input.file;
}

// Build output object used by downstream nodes
return {
  json: {
    aggregatedData: aggregatedData,
    hasPreviousReport: previousReportFile !== null,
    previousReportId: previousReportFile?.id || null,
    previousReportName: previousReportFile?.name || null,
    previousReportDate: previousReportFile?.modifiedTime || previousReportFile?.createdTime || null
  }
};

// Parse Previous Report - Extract content and merge with aggregated data
const prevReportData = $('Get Previous Report').first().json;
const aggregatedData = prevReportData.aggregatedData;

let previousReportContent = null;
let hasPreviousReport = false;
let previousReportName = prevReportData.previousReportName;
let previousReportDate = prevReportData.previousReportDate;

// Only parse if we found a previous report
if (prevReportData.hasPreviousReport && prevReportData.previousReportId) {
  try {
    const downloadResult = $('Download Previous Report');

    if (downloadResult && downloadResult.first()) {
      const downloadedFile = downloadResult.first();

      // Check for binary data
      if (downloadedFile.binary && downloadedFile.binary.data) {
        const binaryData = downloadedFile.binary.data;
        const buffer = Buffer.from(binaryData.data, 'base64');
        previousReportContent = buffer.toString('utf8');
        hasPreviousReport = true;
      }
    }
  } catch (error) {
    console.log('Could not parse previous report:', error.message);
    hasPreviousReport = false;
  }
}

// Return combined data for Generate Prompt
return {
  json: {
    ...aggregatedData,
    previousReport: {
      hasPreviousReport: hasPreviousReport,
      reportName: previousReportName,
      reportDate: previousReportDate,
      content: previousReportContent
    }
  }
};

// Extract and validate input parameters
const targetUrl = $input.first().json.target_url || $input.first().json.body?.target_url;
const location = $input.first().json.location || $input.first().json.body?.location || 'United Kingdom';
const email = $input.first().json.email || $input.first().json.body?.email;
const pastReportId = $input.first().json.past_report_id || $input.first().json.body?.past_report_id;

// Report type: progress, onboarding, audit, etc.
const reportType = $input.first().json.report_type || $input.first().json.body?.report_type || 'progress';

// Report audience: client or internal
const reportAudience = $input.first().json.report_audience || $input.first().json.body?.report_audience || 'client';

// Date range parameters
const dateFrom = $input.first().json.date_from || $input.first().json.body?.date_from || null;
const dateTo = $input.first().json.date_to || $input.first().json.body?.date_to || null;

// Validate inputs
if (!targetUrl) {
  throw new Error('target_url is required');
}

// Clean URL
const cleanUrl = targetUrl.replace(/^https?:\/\//, '').replace(/\/$/, '');

// Calculate default dates if not provided (last 30 days)
const today = new Date();
const todayStr = today.toISOString().split('T')[0];
const thirtyDaysAgo = new Date(today);
thirtyDaysAgo.setDate(thirtyDaysAgo.getDate() - 30);
const thirtyDaysAgoStr = thirtyDaysAgo.toISOString().split('T')[0];

const finalDateFrom = dateFrom || thirtyDaysAgoStr;
const finalDateTo = dateTo || todayStr;

return {
  json: {
    target_url: cleanUrl,
    location: location,
    report_type: reportType.toLowerCase(),        // 'progress', 'onboarding', etc.
    report_audience: reportAudience.toLowerCase(), // 'client' or 'internal'
    email: email,
    past_report_id: pastReportId,
    date_from: finalDateFrom,
    date_to: finalDateTo,
    timestamp: new Date().toISOString(),
    is_progress_report: reportType.toLowerCase() === 'progress'
  }
};

// Wait for OnPage task to complete (5 minutes)
await new Promise(resolve => setTimeout(resolve, 300000));
return $input.all();

// Parse Historical Domain Rank Overview
const input = $input.first().json;

// Check if API call was successful
if (input.status_code !== 20000 || !input.tasks?.[0]?.result?.[0]) {
  return {
    success: false,
    error: input.tasks?.[0]?.status_message || 'Historical Domain Rank API failed',
    domainRank: null
  };
}

const result = input.tasks[0].result[0];
const items = result.items || [];

// Get the most recent and oldest data points
const sortedItems = items.sort((a, b) => new Date(b.date) - new Date(a.date));
const latestData = sortedItems[0] || {};
const oldestData = sortedItems[sortedItems.length - 1] || {};

const latestMetrics = latestData.metrics?.organic || {};
const oldestMetrics = oldestData.metrics?.organic || {};

// Calculate changes over the period
const keywordChange = (latestMetrics.count || 0) - (oldestMetrics.count || 0);
const trafficChange = (latestMetrics.etv || 0) - (oldestMetrics.etv || 0);

return {
  success: true,
  domainRank: {
    target: result.target,
    location: result.location_code,
    language: result.language_code,

    // Date range
    dateFrom: $('Validate Input1').first().json.date_from || 'N/A',
    dateTo: $('Validate Input1').first().json.date_to || 'N/A',
    dataPoints: items.length,

    // Current (Latest) Positions
    positions: {
      top3: (latestMetrics.pos_1 || 0) + (latestMetrics.pos_2_3 || 0),
      top10: (latestMetrics.pos_1 || 0) + (latestMetrics.pos_2_3 || 0) + (latestMetrics.pos_4_10 || 0),
      page2: latestMetrics.pos_11_20 || 0,
      page3: latestMetrics.pos_21_30 || 0,
      page4Plus: (latestMetrics.pos_31_40 || 0) + (latestMetrics.pos_41_50 || 0) + 
                 (latestMetrics.pos_51_60 || 0) + (latestMetrics.pos_61_70 || 0) + 
                 (latestMetrics.pos_71_80 || 0) + (latestMetrics.pos_81_90 || 0) + 
                 (latestMetrics.pos_91_100 || 0)
    },

    // Traffic & Value (Current)
    estimatedTrafficValue: Math.round(latestMetrics.etv || 0),
    totalKeywords: latestMetrics.count || 0,

    // Changes Over Period
    periodChanges: {
      keywordsChange: keywordChange,
      trafficValueChange: Math.round(trafficChange),
      keywordsStart: oldestMetrics.count || 0,
      keywordsEnd: latestMetrics.count || 0,
      trafficStart: Math.round(oldestMetrics.etv || 0),
      trafficEnd: Math.round(latestMetrics.etv || 0)
    },

    // Movement (from latest data)
    movement: {
      newKeywords: latestMetrics.is_new || 0,
      improved: latestMetrics.is_up || 0,
      declined: latestMetrics.is_down || 0,
      lost: latestMetrics.is_lost || 0
    }
  }
};

// Parse Ranked Keywords - Extract and analyze keyword data
const input = $input.first().json;

// Check if API call was successful
if (input.status_code !== 20000 || !input.tasks?.[0]?.result?.[0]) {
  return {
    success: false,
    error: input.tasks?.[0]?.status_message || 'Ranked Keywords API failed',
    keywords: null
  };
}

const result = input.tasks[0].result[0];
const items = result.items || [];
const target = result.target || '';

// Extract domain name for branded keyword detection
const domainName = target.replace(/^www\./, '').split('.')[0].toLowerCase();

// Initialize counters
let brandedCount = 0;
let unbrandedCount = 0;
let totalSearchVolume = 0;
let totalETV = 0;

// Position buckets
const positionBuckets = {
  top3: [],
  top10: [],
  page2Opportunities: [],  // Positions 11-20 - Ready to push!
  page3: [],
  deepPositions: []
};

// Process each keyword
const processedKeywords = items.map(item => {
  const keyword = item.keyword_data?.keyword || '';
  const keywordLower = keyword.toLowerCase();
  const searchVolume = item.keyword_data?.keyword_info?.search_volume || 0;
  const rank = item.ranked_serp_element?.serp_item?.rank_absolute || 999;
  const url = item.ranked_serp_element?.serp_item?.url || '';
  const etv = item.ranked_serp_element?.serp_item?.etv || 0;
  const cpc = item.keyword_data?.keyword_info?.cpc || 0;
  const difficulty = item.keyword_data?.keyword_properties?.keyword_difficulty || 0;
  const intent = item.keyword_data?.search_intent_info?.main_intent || 'unknown';
  const isNew = item.ranked_serp_element?.serp_item?.rank_changes?.is_new || false;

  // Determine if branded
  const isBranded = keywordLower.includes(domainName);
  if (isBranded) {
    brandedCount++;
  } else {
    unbrandedCount++;
  }

  totalSearchVolume += searchVolume;
  totalETV += etv;

  const keywordData = {
    keyword,
    rank,
    searchVolume,
    etv: Math.round(etv * 100) / 100,
    cpc: Math.round(cpc * 100) / 100,
    difficulty,
    intent,
    url,
    isBranded,
    isNew
  };

  // Categorize by position
  if (rank <= 3) {
    positionBuckets.top3.push(keywordData);
  } else if (rank <= 10) {
    positionBuckets.top10.push(keywordData);
  } else if (rank <= 20) {
    positionBuckets.page2Opportunities.push(keywordData);
  } else if (rank <= 30) {
    positionBuckets.page3.push(keywordData);
  } else {
    positionBuckets.deepPositions.push(keywordData);
  }

  return keywordData;
});

// Sort opportunities by search volume (highest first)
positionBuckets.page2Opportunities.sort((a, b) => b.searchVolume - a.searchVolume);
positionBuckets.top10.sort((a, b) => a.rank - b.rank);
positionBuckets.top3.sort((a, b) => a.rank - b.rank);

// Calculate branded/unbranded percentages
const totalKeywords = items.length;
const brandedPercent = totalKeywords > 0 ? Math.round((brandedCount / totalKeywords) * 100) : 0;
const unbrandedPercent = totalKeywords > 0 ? Math.round((unbrandedCount / totalKeywords) * 100) : 0;

return {
  success: true,
  keywords: {
    // Summary Stats
    summary: {
      totalKeywords,
      totalSearchVolume,
      totalETV: Math.round(totalETV * 100) / 100,
      brandedCount,
      unbrandedCount,
      brandedPercent,
      unbrandedPercent
    },

    // Position Distribution
    distribution: {
      top3Count: positionBuckets.top3.length,
      top10Count: positionBuckets.top3.length + positionBuckets.top10.length,
      page2Count: positionBuckets.page2Opportunities.length,
      page3Count: positionBuckets.page3.length,
      deepCount: positionBuckets.deepPositions.length
    },

    // Top Performers (Top 10 by rank)
    topPerformers: [...positionBuckets.top3, ...positionBuckets.top10].slice(0, 10),

    // Quick Win Opportunities (Page 2 keywords with high volume)
    quickWinOpportunities: positionBuckets.page2Opportunities.slice(0, 10),

    // All keywords for detailed analysis
    allKeywords: processedKeywords.slice(0, 50)  // Limit to 50 for report
  }
};

// Parse OnPage Summary - Extract technical SEO metrics
const input = $input.first().json;
const task = input.tasks?.[0];

// Check if task is still in queue
if (task?.status_code === 40602) {
  return {
    success: false,
    status: 'in_queue',
    message: 'OnPage analysis is still processing. Please wait and retry.',
    onPage: null
  };
}

// Check if API call was successful
if (input.status_code !== 20000 || !task?.result?.[0]) {
  return {
    success: false,
    status: 'error',
    error: task?.status_message || 'OnPage API failed',
    onPage: null
  };
}

const result = task.result[0];
const crawl = result.crawl_progress || {};

return {
  success: true,
  status: 'complete',
  onPage: {
    // Crawl Info
    crawlProgress: crawl,
    pagesCrawled: result.pages_crawled || 0,
    pagesInQueue: result.pages_in_queue || 0,

    // Technical Issues
    issues: {
      critical: result.page_metrics?.checks?.critical || 0,
      warnings: result.page_metrics?.checks?.warnings || 0,
      notices: result.page_metrics?.checks?.notices || 0
    },

    // Page Metrics
    pageMetrics: {
      brokenLinks: result.page_metrics?.broken_links || 0,
      brokenResources: result.page_metrics?.broken_resources || 0,
      duplicateTitle: result.page_metrics?.duplicate_title || 0,
      duplicateDescription: result.page_metrics?.duplicate_description || 0,
      duplicateContent: result.page_metrics?.duplicate_content || 0
    },

    // Performance
    performance: {
      avgPageLoadTime: result.page_metrics?.page_timing?.time_to_interactive || 0,
      https: result.page_metrics?.checks?.no_https || 0,
      mobileUsability: result.page_metrics?.checks?.is_mobile_friendly || false
    }
  }
};

// Parse Historical Backlinks
const input = $input.first().json;
const task = input.tasks?.[0];

if (input.status_code !== 20000 || !task?.result?.[0]) {
  return {
    success: false,
    error: task?.status_message || 'Historical Backlinks API failed',
    backlinks: null
  };
}

const result = task.result[0];
const items = result.items || [];

// Sort by date
