// --- Create a slug that matches how filenames are generated ---

// 1) Base on the domain (no protocol, no path)
const domainOnly = targetUrl
  .replace(/^https?:\/\//, '')
  .replace(/^www\./, '')
  .split('/')[0]; // strip any path

// 2) Apply same cleaning as in Convert To Markdown1/2
const websiteSlug = domainOnly
  .replace(/[^a-zA-Z0-9]/g, '_')
  .replace(/_+/g, '_')
  .toLowerCase();

console.log('Website slug for matching:', websiteSlug);

// NOTE: We intentionally do NOT require exact date range match here.
// We just want the latest SEO_Performance_Report for this site,
// regardless of what period that previous report used.

// Filter matching files
const matchedFiles = inputFiles.filter(file => {
  const name = (file.name || '').toLowerCase();

  const hasReport = name.includes('seo_performance_report');
  const hasUrl = name.includes(websiteSlug);

  const isMatch = hasReport && hasUrl;

  console.log(`Checking file: ${file.name}`);
  console.log(`  - hasReport: ${hasReport}`);
  console.log(`  - hasUrl (${websiteSlug}): ${hasUrl}`);
  console.log(`  - MATCH: ${isMatch}`);

  return isMatch;
});

console.log(`Total matched files: ${matchedFiles.length}`);

if (matchedFiles.length === 0) {
  return [{
    json: {
      file: null,
      matched: false,
      debug: {
        targetUrl,
        websiteSlug,
        totalFiles: inputFiles.length,
        sampleFilename: inputFiles[0]?.name
      }
    }
  }];
}

// Sort by modifiedTime descending (latest first)
matchedFiles.sort((a, b) => {
  const aTime = new Date(a.modifiedTime || a.createdTime || 0);
  const bTime = new Date(b.modifiedTime || b.createdTime || 0);
  return bTime - aTime;
});

console.log(`Latest matched file: ${matchedFiles[0].name}`);

return [{
  json: {
    file: matchedFiles[0],
    matched: true,
    totalMatches: matchedFiles.length
  }
}];
